const initialState = {
  images: [],
  selectedImage: null,
  isModalActive: false,
};

function appReducer(state = initialState, { type, payload }) {
  switch (type) {
    case 'UPLOAD_IMAGES':
      // eslint-disable-next-line no-console
      console.log(payload);

      return {
        ...state, images: payload,
      };
    case 'TOGGLE_MODAL':
      return {
        ...state, isModalActive: !state.isModalActive,
      };
    case 'SELECT_IMAGE':
      // eslint-disable-next-line no-console
      console.log(payload);

      return {
        ...state,
        selectedImage: payload,
      };
    default:
      return state;
  }
}

export default appReducer;
