const initialState = {
  images: [],
  selectedImage: null,
};

function appReducer(state = initialState, { type, payload }) {
  switch (type) {
    case 'UPLOAD_IMAGES':
      return {
        ...state, images: payload,
      };
    case 'SELECT_IMAGE':
      console.log(payload)
      return {
        ...state,
        selectedImage: payload,
      };
    default:
      return state;
  }
}

export default appReducer;
